(64,	'sirt-woo-2024-11-xss-admin',	'update',	'en_US',	'An important update is required for WooCommerce',	'This update addresses a security vulnerability that could allow unauthorized access to administrative privileges on your site. <a href=\"https://developer.woocommerce.com/2024/12/03/woocommerce-9-4-3-and-woocommerce-9-3-4-available-now/\">Learn more</a>.\r\n<br><br>\r\n<strong>Please ensure your site is updated to the latest version to stay secure.</strong>',	'{}',	'pending',	'woocommerce.com',	'2024-12-31 05:17:49',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(65,	'woocommerce-analytics-order-attribution-promotions-december-2024',	'marketing',	'en_US',	'Discover what drives your sales',	'Use our powerful <strong>Order Attribution</strong> extension to understand what truly drives your revenue. Track your sales journey, identify your most effective marketing channels, and optimize your sales strategy.',	'{}',	'pending',	'woocommerce.com',	'2024-12-31 05:17:49',	NULL,	0,	'plain',	'',	0,	0,	'info'),
