(67,	'paypal-braintree-sdk-depreciation-2025-02-20',	'update',	'en_US',	'Update required for your PayPal Braintree extension',	'To continue securely accepting payments, you must update your PayPal Braintree extension to version 3.2.4 or newer before June 30, 2025. Your current extension version will not support payment processing after this date due to an SSL certificate update.',	'{}',	'pending',	'woocommerce.com',	'2025-03-26 06:33:05',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(68,	'woo-marketplace-two-year-subscriptions-rin-2025-03-17',	'marketing',	'en_US',	'Save 20% on new two-year subscriptions',	'Weve introduced 20% off two-year subscriptions for all products* in the WooCommerce Marketplace. Lock in long-term savings and make sure your store never misses a beat. \r\n\r\n*Some exclusions apply. New purchases only.',	'[]',	'pending',	'woocommerce.com',	'2025-03-26 06:33:05',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(69,	'stripe_orderstatus_bug_q2_2025',	'update',	'en_US',	'Action Required: Update Order Status Manager',	'Update to the latest Order Status Manager (v1.15.5+) to ensure payments are captured. Your version of WooCommerce Order Status Manager can interfere with order processing for payment gateways (including WooCommerce Stripe Gateway v9.2.0+), which may cause orders to process without capturing payment.',	'{}',	'pending',	'woocommerce.com',	'2025-04-12 12:50:39',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(70,	'WooCommerce-Stripe-Gateway-Update-April-2025',	'update',	'en_US',	'Action required: Update WooCommerce Stripe Gateway to v9.4.1',	'<strong>An important update for WooCommerce Stripe Payment Gateway has been released</strong>. Version 9.4.1 addresses performance issues and payment method visibility problems that occurred in version 9.4.0. Please update as soon as possible for optimal store performance. Please read <a href=\"https://developer.woocommerce.com/2025/04/18/https://developer.woocommerce.com/2025/04/18/developer-advisory-stripe-payment-gateway-9-4-1-release/\" /> this advisory</a> for more information.',	'{}',	'pending',	'woocommerce.com',	'2025-04-22 08:09:23',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(71,	'WooCommerce-Stripe-Gateway-Update-Info-April-2025',	'update',	'en_US',	'WooCommerce Stripe Gateway has been updated to v9.4.1',	'<strong>WooCommerce Stripe Payment Gateway was recently updated</strong>. Version 9.4.1 addresses performance issues and payment method visibility problems that occurred in version 9.4.0. No further action is required. Please read <a href=\"https://developer.woocommerce.com/2025/04/18/https://developer.woocommerce.com/2025/04/18/developer-advisory-stripe-payment-gateway-9-4-1-release/\" /> this advisory post </a>for more information.',	'{}',	'pending',	'woocommerce.com',	'2025-04-22 08:09:23',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(72,	'klaviyo_q2_2025',	'marketing',	'en_US',	'Tailor your marketing with data-driven automation',	'Deliver personalized experiences at scale, simplify your stack, and make your data work for you using Klaviyos best-in-class marketing extension for Woo. With advanced segmentation based on actual customer behavior, you can send email and SMS messages that drive revenue and increase loyalty  without adding complexity',	'{}',	'pending',	'woocommerce.com',	'2025-04-30 05:19:54',	NULL,	0,	'plain',	'',	0,	0,	'info'),
