(74,	'uk-vat-notice-2025-04-30',	'warning',	'en_US',	'UK VAT numbers are no longer supported',	'Due to recent HM Revenue &amp; Customs (HMRC) VAT API changes, your EU VAT Number for WooCommerce extension can no longer validate UK VAT numbers. HMRC now requires merchants to register an API key, which this extension does not currently support. We recommend EU/UK VAT Validation Manager for WooCommerce as a replacement',	'{}',	'pending',	'woocommerce.com',	'2025-06-03 05:09:47',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(75,	'payson-retirement-2025',	'update',	'en_US',	'Payson Form Retirement',	'Svea Bank is discontinuing the Payson service; the last date for transactions is August 31, 2025. As a result, the Payson Form extension has been removed from WooCommerce.com. Documentation remains available.',	'{}',	'pending',	'woocommerce.com',	'2025-06-03 05:09:47',	NULL,	0,	'plain',	'',	0,	0,	'info'),
